import {SET_ALL_POSTS} from '../Actions/postAction';
import {SET_ACTIVE_POST} from '../Actions/postAction';

export function getAllPostsReducer(state={data:[]}, action) {
    switch(action.type) {
        case SET_ALL_POSTS:
            return {
                ...state,
                posts: action.payload
            };


        default:
            return state;
    }
}

export function getActivePost(state={activePost:[]}, action){
    switch(action.type) {
            case SET_ACTIVE_POSTS:
            return {
                ...state,
                activePost:action.payload
            }

    } 

}
