import React from 'react';
import axios from 'axios';
import { Card,CardTitle, CardText} from 'react-toolbox/lib/card';

export default class PostInfo extends React.Component {
    state = {
        users: [],
        posts: []
    }

    componentDidMount() {
        axios.get(`https://jsonplaceholder.typicode.com/posts`).then(res => {
            console.log(this.props.match.params.postId);
            this.setState({ posts: res.data.filter(post => post.id === parseInt(this.props.match.params.postId)) });
            console.log(this.state.posts)
        });
        axios.get(`https://jsonplaceholder.typicode.com/users`).then(res => {
            console.log(this.props.match.params.userId)
            this.setState({ users: res.data.filter(user => user.id === parseInt(this.props.match.params.userId)) });
             console.log(this.state.users)
        });
    }

    render() {
        return (
            <div>
                 <Card style={{width: '400px'}}>
                     <CardTitle title="About post: "/>
                <CardText>{this.state.posts.map(post => post.body)}</CardText>
                 </Card>

                 <Card style={{width: '400px'}}>
                     <CardTitle title="About user: "/>
                <CardText>ID:{this.state.users.map(user => user.id)}}</CardText>
                <CardText>Name:{this.state.users.map(user => user.name)}</CardText>

                 </Card>

            </div>

        )
    }
}
