import React from 'react';
import './style.css';
import service from '../Service/service';
import {getAllUsers} from '../Actions/userAction';
import {connect} from 'react-redux';


 class PostInfo extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoaded: false,
        }
    }

    async componentDidMount() {

        try {
            const users = await service.getAllUsers();
            this.props.getUsers(users);
            this.setState({
                isLoaded: true
            })
            console.log(users)
        } catch(error) {
            throw new Error(error); 
        }
    }

    render() {
        let { isLoaded } = this.state;
        if(!isLoaded) {
            return<div>Loading..</div>
        } else {
            return (
                <div>
                    <div className="card">
                        <h2>About Post:</h2> 
                        <p className='post-info'>{this.state.posts.body}</p>
                    </div>
                    <div className="card">
                        <h2>About User</h2>
                     <ul className="user-info">
                            <li><p>ID</p>{this.state.user.id}</li>
                            <li><p>Name</p>{this.state.user.name}</li>
                            <li><p>Username</p>{this.state.user.username}</li>
                            <li><p>Email</p>{this.state.user.email}</li>
                        </ul>
                    </div>
                </div>
             )
        }
    }
}


const mapStateToProps = state => ({
    users: state.usersRequestResults.users
});
const mapDispatchToProps = dispatch => ({
    getUsers: (users) => {
        dispatch(getAllUsers(users));
    }
});

export default connect(mapStateToProps, mapDispatchToProps)(PostInfo);
