import {SET_ALL_POSTS, SET_ACTIVE_POST} from '../Actions/postAction';


export function getAllPostsReducer(state={data:[]}, action) {
    switch(action.type) {
        case SET_ALL_POSTS:
            return {
                ...state,
                posts: action.payload
            };

        case SET_ACTIVE_POSTS:
            return {
                ...state,
                activePost:action.payload
            }

        default:
            return state;
    }
}
