import React from 'react';
import axios from 'axios';

export default class PostInfo extends React.Component {
    state = {
        users: [],
        posts: []
    }

    componentDidMount() {
        axios.get(`https://jsonplaceholder.typicode.com/posts/${this.props.match.params.postId}`).then(res => {
            this.setState({ posts: res.data});
        });
        axios.get(`https://jsonplaceholder.typicode.com/users/${this.props.match.params.userId}`).then(res => {
            this.setState({ users: res.data});
        });
    }

    render() {
        return (
            <div>
                <h1>About Post:</h1> 
                <p>{this.state.posts.filter(post => post.body)}</p>
                <h1>About User:</h1>
                <p>ID:{this.state.users.map(user => user.id)}</p>
                <p>Name:{this.state.users.map(user => user.name)}</p>
                <p>Username:{this.state.users.map(user => user.username)}</p>
                <p>Email:{this.state.users.map(user => user.email)}</p>
            </div>

        )
    }
}
