import React from 'react';
import axios from 'axios';

export default class PostInfo extends React.Component {
    state = {
        users: [],
        posts: []
    }

    matchParams = this.props.match.params
    componentDidMount() {
        axios.get(`https://jsonplaceholder.typicode.com/posts/${this.matchParams.postId}`).then(res => {
            console.log(this.props.match.params.postId);
            this.setState({ posts: res.data});
            console.log(this.state.posts)
        });
        axios.get(`https://jsonplaceholder.typicode.com/users`).then(res => {
            console.log(this.props.match.params.userId)
            this.setState({ users: res.data.filter(user => user.id === parseInt(this.matchParams.userId)) });
             console.log(this.state.users)
        });
    }

    render() {
        return (
            <div>
                <p>About Post:</p> 
                <p>{this.state.posts.map(post => post.body)}</p>
                <ul>
                    <li>About User:</li>
                    <li>ID:{this.state.users.map(user => user.id)}</li>
                    <li>Name:{this.state.users.map(user => user.name)}</li>
                    <li>Username:{this.state.users.map(user => user.username)}</li>
                    <li>Email:{this.state.users.map(user => user.email)}</li>
                </ul>

            </div>

        )
    }
}
